import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Home() {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/api/countries')
      .then(response => {
        setCountries(response.data);
      })
      .catch(error => {
        console.error('Error fetching countries:', error);
      });
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Countries</h1>
      <ul>
        {countries.map(country => (
          <li key={country.countryCode} className="mb-2">
            {country.flag && ( <img src={country.flag} alt={`${country.name} flag`} className="w-6 h-6 inline-block mr-2" /> )}
            <Link to={`/country/${country.countryCode}`} className="text-blue-500 hover:underline">
              - {country.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
