import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function CountryDetails() {
  const { countryCode } = useParams();
  const [countryInfo, setCountryInfo] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3001/api/country/${countryCode}`)
      .then(response => {
        setCountryInfo(response.data);
      })
      .catch(error => {
        console.error('Error fetching country details:', error);
      });
  }, [countryCode]);

  if (!countryInfo) {
    return <div>Loading...</div>;
  }


  console.log(countryInfo.borders);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">{countryInfo.country.name}</h1>
      <p><strong>Region:</strong> {countryInfo.region}</p>
      <p><strong>Borders:</strong></p>
      <ul>
        {countryInfo.borders.map((border, index) => (
          <li key={index}>{border.commonName} ({border.region})</li>
        ))}
      </ul>
      <p><strong>Population Counts:</strong></p>
      <ul>
        {countryInfo.populationCounts.map((count, index) => (
          <li key={index}>{count.year}: {count.value}</li>
        ))}
      </ul>
    </div>
  );
}

export default CountryDetails;
